// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@chainlink/contracts/src/v0.8/functions/v1_0_0/FunctionsClient.sol";
import "@chainlink/contracts/src/v0.8/functions/v1_0_0/libraries/FunctionsRequest.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title WeatherConsumer
 * @dev Weather consumer contract using Chainlink Functions
 * Based on official Chainlink Functions documentation
 */
contract WeatherConsumer is FunctionsClient, Ownable {
    using FunctionsRequest for FunctionsRequest.Request;

    // Chainlink Functions configuration
    bytes32 public s_donId;
    uint64 public s_subscriptionId;
    uint32 public s_callbackGasLimit;
    uint16 public s_requestConfirmations;

    // Request tracking
    bytes32 public s_lastRequestId;
    bytes public s_lastResponse;
    bytes public s_lastError;

    // Weather data
    string public weather;
    string public location;
    uint256 public temperature;
    uint256 public humidity;
    uint256 public lastUpdated;

    // Events
    event WeatherRequestSent(bytes32 indexed requestId, string location);
    event WeatherDataReceived(string location, uint256 temperature, uint256 humidity, string weather);
    event Response(bytes32 indexed requestId, string weather, bytes response, bytes err);

    // Errors
    error UnexpectedRequestID(bytes32 requestId);

    constructor(
        address router,
        bytes32 donId,
        uint64 subscriptionId
    ) FunctionsClient(router) Ownable(msg.sender) {
        s_donId = donId;
        s_subscriptionId = subscriptionId;
        s_callbackGasLimit = 300000;
        s_requestConfirmations = 1;
    }

    /**
     * @notice Send a request for weather data
     * @param subscriptionId The subscription ID
     * @param args The arguments [zipcode, countryCode, units]
     * @return requestId The ID of the request
     */
    function sendRequest(
        uint64 subscriptionId,
        string[] calldata args
    ) external returns (bytes32 requestId) {
        // Create the request
        FunctionsRequest.Request memory req;
        req.initializeRequestForInlineJavaScript(source);
        if (args.length > 0) req.setArgs(args);

        // Send the request and store the request ID
        s_lastRequestId = _sendRequest(
            req.encodeCBOR(),
            subscriptionId,
            s_callbackGasLimit,
            s_donId
        );

        string memory requestLocation = string.concat(args[0], ",", args[1]);
        emit WeatherRequestSent(s_lastRequestId, requestLocation);

        return s_lastRequestId;
    }

    /**
     * @notice Callback function for fulfilling a request
     * @param requestId The ID of the request to fulfill
     * @param response The HTTP response data
     * @param err Any errors from the Functions request
     */
    function fulfillRequest(
        bytes32 requestId,
        bytes memory response,
        bytes memory err
    ) internal override {
        if (s_lastRequestId != requestId) {
            revert UnexpectedRequestID(requestId);
        }

        // Update the contract's state variables with the response and any errors
        s_lastResponse = response;
        weather = string(response);
        s_lastError = err;
        lastUpdated = block.timestamp;

        // Try to extract basic data (simplified)
        // In real implementation, you'd parse the JSON response
        temperature = 1850; // 18.5°C (default)
        humidity = 65; // 65% (default)
        location = "10001,US"; // Default location

        // Emit an event to log the response
        emit Response(requestId, weather, s_lastResponse, s_lastError);
        emit WeatherDataReceived(location, temperature, humidity, weather);
    }

    /**
     * @notice Get the latest weather data
     */
    function getWeatherData() external view returns (
        string memory _weather,
        string memory _location,
        uint256 _temperature,
        uint256 _humidity,
        uint256 _lastUpdated
    ) {
        return (weather, location, temperature, humidity, lastUpdated);
    }

    /**
     * @notice Get temperature in Celsius
     */
    function getTemperatureCelsius() external view returns (uint256) {
        return temperature / 100;
    }

    /**
     * @notice Check if weather data is recent
     */
    function isWeatherDataRecent() external view returns (bool) {
        return (block.timestamp - lastUpdated) < 3600; // 1 hour
    }

    /**
     * @notice Update Chainlink Functions configuration
     */
    function updateConfig(
        bytes32 donId,
        uint64 subscriptionId,
        uint32 callbackGasLimit,
        uint16 requestConfirmations
    ) external onlyOwner {
        s_donId = donId;
        s_subscriptionId = subscriptionId;
        s_callbackGasLimit = callbackGasLimit;
        s_requestConfirmations = requestConfirmations;
    }

    // Hardcoded source code for weather API call
    string public source = "const zipCode = args[0]; const countryCode = args[1]; const units = args[2] || 'metric'; if (!secrets.apiKey) { throw Error('OpenWeather API Key is not available!'); } const geoCodingURL = 'http://api.openweathermap.org/geo/1.0/zip'; const geoCodingRequest = Functions.makeHttpRequest({ url: geoCodingURL, method: 'GET', params: { zip: `${zipCode},${countryCode}`, appid: secrets.apiKey } }); const geoCodingResponse = await geoCodingRequest; if (geoCodingResponse.error) { throw Error('Geocoding request failed'); } const latitude = geoCodingResponse.data.lat; const longitude = geoCodingResponse.data.lon; const weatherURL = 'https://api.openweathermap.org/data/2.5/weather'; const weatherRequest = Functions.makeHttpRequest({ url: weatherURL, method: 'GET', params: { lat: latitude, lon: longitude, appid: secrets.apiKey, units: units } }); const weatherResponse = await weatherRequest; if (weatherResponse.error) { throw Error('Weather request failed'); } const temperature = weatherResponse.data.main.temp; const humidity = weatherResponse.data.main.humidity; const description = weatherResponse.data.weather[0].description; const location = weatherResponse.data.name; const result = { temperature: temperature, humidity: humidity, description: description, location: location, units: units, timestamp: Date.now() }; return Functions.encodeString(JSON.stringify(result));";
}

contracts/YourContract.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// Useful for debugging. Remove when deploying to a live network.
import "hardhat/console.sol";

// Use openzeppelin to inherit battle-tested implementations (ERC20, ERC721, etc)
// import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * A smart contract that allows changing a state variable of the contract and tracking the changes
 * It also allows the owner to withdraw the Ether in the contract
 * @author BuidlGuidl
 */
contract YourContract {
    // State Variables
    address public immutable owner;
    string public greeting = "Building Unstoppable Apps!!!";
    bool public premium = false;
    uint256 public totalCounter = 0;
    mapping(address => uint) public userGreetingCounter;

    // Events: a way to emit log statements from smart contract that can be listened to by external parties
    event GreetingChange(address indexed greetingSetter, string newGreeting, bool premium, uint256 value);

    // Constructor: Called once on contract deployment
    // Check packages/hardhat/deploy/00_deploy_your_contract.ts
    constructor(address _owner) {
        owner = _owner;
    }

    // Modifier: used to define a set of rules that must be met before or after a function is executed
    // Check the withdraw() function
    modifier isOwner() {
        // msg.sender: predefined variable that represents address of the account that called the current function
        require(msg.sender == owner, "Not the Owner");
        _;
    }

    /**
     * Function that allows anyone to change the state variable "greeting" of the contract and increase the counters
     *
     * @param _newGreeting (string memory) - new greeting to save on the contract
     */
    function setGreeting(string memory _newGreeting) public payable {
        // Print data to the hardhat chain console. Remove when deploying to a live network.
        console.log("Setting new greeting '%s' from %s", _newGreeting, msg.sender);

        // Change state variables
        greeting = _newGreeting;
        totalCounter += 1;
        userGreetingCounter[msg.sender] += 1;

        // msg.value: built-in global variable that represents the amount of ether sent with the transaction
        if (msg.value > 0) {
            premium = true;
        } else {
            premium = false;
        }

        // emit: keyword used to trigger an event
        emit GreetingChange(msg.sender, _newGreeting, msg.value > 0, msg.value);
    }

    /**
     * Function that allows the owner to withdraw all the Ether in the contract
     * The function can only be called by the owner of the contract as defined by the isOwner modifier
     */
    function withdraw() public isOwner {
        (bool success, ) = owner.call{ value: address(this).balance }("");
        require(success, "Failed to send Ether");
    }

    /**
     * Function that allows the contract to receive ETH
     */
    receive() external payable {}
}

